// commands:
//  - SHIFT+ENTER: render
//  - drap & drop file: kicad import


// constants
const width = 1.06 // board width
const height = .83 // board height
const x = 1 // x origin
const y = 1 // y origin
const zt = 0 // top z
const zb = -0.06 // bottom z
const w = .015 // wire width
const mask = .004 // solder mask size
const border = 0.05 // rendering border


/* -- DECLARE_PCB -- */
let board = new PCB();

let interior = geo.translate(geo.rectangle(width, height), [x+width/2, y+height/2]);


/* -- ADD_COMPONENTS -- */
let IC1 = board.add(AVRDB32, {translate: pt(x+.56, y+.38), name: 'IC1\n128DB32'});
let J1 = board.add(header_FTDI, {translate: pt(x+.23, IC1.posY-.03), rotate: 180, name: 'J1\nserial\n5V'});
let J2 = board.add(header_UPDI, {translate: pt(x+width-.23, IC1.posY+.23), rotate: 180, name: 'J2\nUPDI'});
let C1 = board.add(C_1206, {translate: pt(IC1.padX("VIO2"), IC1.posY-.28), name: 'C1 1uF'});
let C2 = board.add(C_1206, {translate: pt(IC1.posX+.32, IC1.padY("AVDD")), rotate: 90, name: 'C2\n1uF'});
let C3 = board.add(C_1206, {translate: pt(IC1.posX, IC1.posY+.35), name: 'C3 1uF'});

board.addShape("interior", interior);


/* -- ADD_WIRES -- */
board.wire([IC1.pad("AVDD"),
            pt(IC1.padX("VIO2"), IC1.padY("AVDD")),
            IC1.pad("VIO2")], w);

board.wire([IC1.pad("AVDD"),
            pt(IC1.padX("VDD"), IC1.padY("AVDD")),
            IC1.pad("VDD")], w);

board.wire([IC1.pad("PA1"),
            pt(IC1.padX("PA1"), IC1.padY("PA1")+.07),
            pt(J1.posX+.09, IC1.padY("PA1")+.07),
            pt(J1.posX+.09, J1.padY("Tx")),
            J1.pad("Tx")], w);

board.wire([IC1.pad("PA0"),
            pt(IC1.padX("PA0"), IC1.padY("PA0")+.1),
            pt(J1.posX-.09, IC1.padY("PA0")+.1),
            pt(J1.posX-.09, J1.padY("Rx")),
            J1.pad("Rx")], w);

board.wire([J2.pad("UPDI"),
            pt(IC1.padX("UPDI"), J2.padY("UPDI")),
            IC1.pad("UPDI")], w);

board.wire([J1.pad("GND"),
            pt(C1.padX("1"), J1.padY("GND"))], w);

board.wire([IC1.pad("VIO2"),
            pt(C1.posX, C1.posY),
            C1.pad("2")], w);

board.wire([IC1.pad("GND1"),
            pt(IC1.padX("GND1")+.07, IC1.padY("GND1")),
            pt(IC1.padX("GND1")+.07, C2.padY("2")+.07),
            pt(C2.posX+.07, C2.padY("2")+.07),
            pt(C2.posX+.07, C2.padY("1")),
            C2.pad("1")], w);

board.wire([J2.pad("GND"),
            pt(C2.posX+.07, J2.padY("GND")),
            pt(C2.posX+.07, C2.padY("1")),
            C2.pad("1")], w);

board.wire([IC1.pad("AVDD"),
            pt(C2.posX, C2.posY),
            C2.pad("2")], w);

board.wire([C2.pad("1"),
            pt(C2.posX, C1.posY-.07),
            pt(C1.padX("1"), C1.posY-.07),
            C1.pad("1")], w);

board.wire([IC1.pad("GND2"),
            pt(IC1.padX("GND2"), C3.posY),
            C3.pad("1")], w);

board.wire([C3.pad("1"),
            pt(J1.posX-.12, C3.posY),
            pt(J1.posX-.12, J1.padY("VCC")+.05),
            pt(J1.posX+.09, J1.padY("VCC")+.05),
            pt(J1.posX+.09, J1.padY("GND")),
            J1.pad("GND")], w);

board.wire([IC1.pad("VDD"),
            pt(IC1.padX("VDD"), C3.posY),
            C3.pad("2")], w);

board.wire([C3.pad("2"),
            pt(C3.padX("2"), C3.posY+.07),
            pt(J1.posX-.15, C3.posY+.07),
            pt(J1.posX-.15, J1.padY("VCC")),
            J1.pad("VCC")], w);


// rendering
renderPCB({
  pcb: board,
  layerColors: {
    "interior": "#002d00ff",
    "B.Cu": "#ff4c007f",
    "F.Cu": "#ff8c00cc",
    "drill": "#ff3300e5",
    "padLabels": "#ffff99e5",
    "componentLabels": "#00e5e5e5",
  },
  limits: {
    x: [x-border, x+width+border],
    y: [y-border, y+height+border]
  },
  mm_per_unit: 25.4
})
