// commands:
//  - SHIFT+ENTER: render
//  - drap & drop file: kicad import


// constants
const width = 1.15 // board width
const height = .65 // board height
const x = 1 // x origin
const y = 1 // y origin
const zt = 0 // top z
const zb = -0.06 // bottom z
const w = .015 // wire width
const mask = .004 // solder mask size
const border = 0.05 // rendering border


/* -- DECLARE_PCB -- */
let board = new PCB();

let interior = geo.translate(geo.rectangle(width, height), [x+width/2, y+height/2]);


/* -- ADD_COMPONENTS -- */
let J1 = board.add(header_FTDI, {translate: pt(x+width-.22, y+height/2+.01), rotate: 0, name: 'J1 FTDI\n3.3V'});
let IC1 = board.add(regulator_SOT23, {translate: pt(J1.posX-.18, J1.padY("CTS")), rotate: 90, name: 'IC1\n3.3V'});
let C1 = board.add(C_1206, {translate: pt(IC1.posX-.14, IC1.posY), rotate: 90, name: 'C1\n1uF'});
let RN = board.add(RN4871, {translate: pt(x, J1.posY), rotate: 90, name: '        RN4871'});
let R1 = board.add(R_1206, {translate: pt(C1.posX-.15, C1.padY("1")), name: 'R1\n10k'});

board.addShape("interior", interior);


/* -- ADD_WIRES -- */
board.wire([J1.pad("GND"),
            pt(IC1.padX("gnd"), J1.padY("GND")),
            IC1.pad("gnd")], w);

board.wire([J1.pad("VCC"),
            pt(IC1.padX("in"), J1.padY("VCC")),
            IC1.pad("in")], w);

board.wire([C1.pad("1"),
            pt(IC1.padX("out"), C1.padY("1")),
            IC1.pad("out")], w);

board.wire([C1.pad("2"),
            pt(IC1.padX("gnd"), C1.padY("2")),
            IC1.pad("gnd")], w);

board.wire([RN.pad("GND1"),
            pt(RN.padX("GND1"), J1.padY("RTS")-.05),
            pt(J1.padX("GND")+.15, J1.padY("RTS")-.05),
            pt(J1.padX("GND")+.15, J1.padY("GND")),
            J1.pad("GND")], w);

board.wire([RN.pad("RST_N"),
            pt(R1.padX("1"), RN.padY("RST_N")),
            R1.pad("1")], w);

board.wire([C1.pad("1"),
            R1.pad("2")], w);

board.wire([RN.pad("RX"),
            pt(R1.padX("1"), RN.padY("RX")),
            pt(R1.padX("1"), J1.padY("Rx")-.05),
            pt(J1.padX("Tx")+.1, J1.padY("Rx")-.05),
            pt(J1.padX("Tx")+.1, J1.padY("Tx")),
            J1.pad("Tx")], w);

board.wire([RN.pad("TX"),
            pt(R1.padX("2"), RN.padY("TX")),
            pt(R1.padX("2"), J1.padY("Rx")),
            J1.pad("Rx")], w);

board.wire([pt(J1.padX("VCC")+.15, J1.padY("VCC")-.05),
            pt(C1.posX, J1.padY("VCC")-.05),
            pt(C1.posX, RN.padY("P3_6")),
            pt(R1.posX, RN.padY("P3_6")),
            pt(R1.posX, J1.padY("GND")-.02),
            pt(RN.padX("GND2"), J1.padY("GND")-.02),
            RN.pad("GND2")], w);

board.wire([R1.pad("2"),
            pt(R1.padX("2"), J1.padY("GND")+.02),
            pt(RN.padX("VBAT"), J1.padY("GND")+.02),
            RN.pad("VBAT")], w);


// rendering
renderPCB({
  pcb: board,
  layerColors: {
    "interior": "#002d00ff",
    "B.Cu": "#ff4c007f",
    "F.Cu": "#ff8c00cc",
    "drill": "#ff3300e5",
    "padLabels": "#ffff99e5",
    "componentLabels": "#00e5e5e5",
  },
  limits: {
    x: [x-border, x+width+border],
    y: [y-border, y+height+border]
  },
  mm_per_unit: 25.4
})
